PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `questions_backup` (`id` INTEGER PRIMARY KEY, `question_sk` TEXT NOT NULL, `question_en` TEXT NOT NULL, `options_sk` JSON, `options_en` JSON, `areas_sk` JSON NOT NULL, `areas_en` JSON NOT NULL, `correct_answer` JSON NOT NULL, `type` TEXT, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Users` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `email` VARCHAR(255) NOT NULL UNIQUE, `password` VARCHAR(255) NOT NULL, `api_key` VARCHAR(255) UNIQUE, `is_admin` TINYINT(1) DEFAULT 0, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `questions` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `question_sk` TEXT NOT NULL, `question_en` TEXT NOT NULL, `options_sk` JSON, `options_en` JSON, `areas_sk` JSON NOT NULL, `areas_en` JSON NOT NULL, `correct_answer` JSON NOT NULL, `type` TEXT, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `tests_history` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `test_date` DATETIME NOT NULL, `city` VARCHAR(255), `country` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `questions_history` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `question_id` INTEGER NOT NULL REFERENCES `questions` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, `test_history_id` INTEGER REFERENCES `tests_history` (`id`) ON DELETE SET NULL ON UPDATE CASCADE, `is_correct` TINYINT(1) NOT NULL DEFAULT 0, `time_taken` INTEGER, `user_answer` JSON NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE INDEX `questions_id` ON `questions` (`id`);
CREATE INDEX `questions_areas_sk` ON `questions` (`areas_sk`);
CREATE INDEX `questions_areas_en` ON `questions` (`areas_en`);
CREATE INDEX `questions_history_question_id` ON `questions_history` (`question_id`);
CREATE INDEX `questions_history_test_history_id` ON `questions_history` (`test_history_id`);
COMMIT;
